import React from "react";
import { useContext } from "react";
import Rodal from "rodal";
import { AppContext } from "../../context/AppProvider";

const RodalPayment = ({
  visiblePopupPayment,
  setVisiblePopupPayment,
  setPaymentType,
  paymentType,
}) => {
  const { mobileMode } = useContext(AppContext);
  return (
    <Rodal
      height={mobileMode ? 200 : 210}
      width={mobileMode ? 350 : 400}
      visible={visiblePopupPayment}
      onClose={() => {
        setVisiblePopupPayment(false);
      }}
      style={{ borderRadius: 10 }}
    >
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          justifyContent: "space-between",
          height: "100%",
        }}
      >
        <div className="rodal-title" style={{ padding: "10px 0 10px 0" }}>
          <span style={{ fontSize: 16, fontWeight: 700 }}>
            Phương thức thanh toán
          </span>
        </div>
        <div
          className="rodal-title f_flex"
          style={{ width: " 100%", gap: 15, flexDirection: "column" }}
        >
          <div
            onClick={() => {
              setPaymentType(0);
              setVisiblePopupPayment(false);
            }}
            className={`f_flex ${paymentType === 0 && "payment-select"}`}
            style={{
              alignItems: "center",
              gap: 15,
              border: "1px solid rgb(230,230,230)",
              padding: 15,
              borderRadius: 10,
              cursor: "pointer",
            }}
          >
            <img src="/images/money.png" alt="" style={{ width: 25 }} />
            <span>Thanh toán khi nhận hàng</span>
          </div>
          <div
            onClick={() => {
              setPaymentType(1);
              setVisiblePopupPayment(false);
            }}
            className={`f_flex ${paymentType === 1 && "payment-select"}`}
            style={{
              display: "none",
              alignItems: "center",
              gap: 15,
              border: "1px solid rgb(230,230,230)",
              padding: 15,
              borderRadius: 10,
              cursor: "pointer",
            }}
          >
            <img
              src="data:image/png;base64,"
              alt=""
              style={{ width: 25 }}
            />
            <span>VN Pay</span>
          </div>
        </div>
      </div>
    </Rodal>
  );
};

export default RodalPayment;
